.fcta-zalo-ben-trong-nut svg path {
   fill: #fff
}

.fcta-zalo-vi-tri-nut {
   position: fixed;
   bottom: 96px;
   right: 20px;
   z-index: 999
}

.fcta-zalo-nen-nut,
div.fcta-zalo-mess {
   box-shadow: 0 1px 6px rgba(0, 0, 0, .06), 0 2px 32px rgba(0, 0, 0, .16)
}

.fcta-zalo-nen-nut {
   width: 50px;
   height: 50px;
   text-align: center;
   color: #fff;
   background: #0068ff;
   border-radius: 50%;
   position: relative
}

.fcta-zalo-nen-nut::after,
.fcta-zalo-nen-nut::before {
   content: "";
   position: absolute;
   border: 1px solid #0068ff;
   background: #0068ff80;
   z-index: -1;
   left: -20px;
   right: -20px;
   top: -20px;
   bottom: -20px;
   border-radius: 50%;
   animation: zoom 1.9s linear infinite
}

.fcta-zalo-nen-nut::after {
   animation-delay: .4s
}

.fcta-zalo-ben-trong-nut,
.fcta-zalo-ben-trong-nut i {
   transition: all 1s
}

.fcta-zalo-ben-trong-nut {
   position: absolute;
   text-align: center;
   width: 60%;
   height: 60%;
   left: 10px;
   bottom: 25px;
   line-height: 70px;
   font-size: 25px;
   opacity: 1
}

.fcta-zalo-ben-trong-nut i {
   animation: lucidgenzalo 1s linear infinite
}

.fcta-zalo-nen-nut:hover .fcta-zalo-ben-trong-nut,
.fcta-zalo-text {
   opacity: 0
}

.fcta-zalo-nen-nut:hover i {
   transform: scale(.5);
   transition: all .5s ease-in
}

.fcta-zalo-text a {
   text-decoration: none;
   color: #fff
}

.fcta-zalo-text {
   position: absolute;
   top: 6px;
   text-transform: uppercase;
   font-size: 12px;
   font-weight: 700;
   transform: scaleX(-1);
   transition: all .5s;
   line-height: 1.5
}

.fcta-zalo-nen-nut:hover .fcta-zalo-text {
   transform: scaleX(1);
   opacity: 1
}

div.fcta-zalo-mess {
   position: fixed;
      bottom: 100px;
    right: 60px;
   z-index: 99;
   background: #fff;
   padding: 7px 25px 7px 15px;
   color: #0068ff;
   border-radius: 50px 0 0 50px;
   font-weight: 700;
   font-size: 15px
}

.fcta-zalo-mess span {
   color: #0068ff !important
}

